mapper.delete:
delete from company_name_record
where pinyin = #{pinyin,jdbcType=VARCHAR}
  and full_name = #{fullName,jdbcType=VARCHAR}

mapper.update:
update company_name_record
set name = #{name,jdbcType=VARCHAR},
  area = #{area,jdbcType=VARCHAR},
  industry = #{industry,jdbcType=VARCHAR},
  type = #{type,jdbcType=VARCHAR},
  update_date = #{updateDate,jdbcType=TIMESTAMP}
where pinyin = #{pinyin,jdbcType=VARCHAR}
  and full_name = #{fullName,jdbcType=VARCHAR}

mapper.select:
select pinyin, full_name, name, area, industry, type, update_date
from company_name_record
where pinyin = #{pinyin,jdbcType=VARCHAR}
  and full_name = #{fullName,jdbcType=VARCHAR}
