mapper.select:
select t3.invoice_amount,t4.contract_id, t4.company_id, t4.company_party_A_name, t4.company_party_B_name, t4.contract_name, t4.contract_amount, t4.invoice_pattern, t4.contract_file, t4.remark, t4.contract_status, t4.contract_date from custom_info t1 inner join company_info t2 on t1.custom_id = t2.custom_id inner join contract_info t4 on t2.company_id = t4.company_id left join invoice_info t3 on t4.contract_id = t3.contract_id
and t1.custom_id = #{customId}
and t2.company_id = #{companyId}
order by t4.contract_date desc

select t4.contract_id, t4.company_id, t4.company_party_A_name, t4.company_party_B_name, t4.contract_name, t4.contract_amount, t4.invoice_pattern, t4.contract_file, t4.remark, t4.contract_status, t4.contract_date from custom_info t1 inner join company_info t2 on t1.custom_id = t2.custom_id inner join contract_info t4 on t2.company_id = t4.company_id left join invoice_info t3 on t4.contract_id = t3.contract_id
and t1.custom_id = #{customId}
and t4.contract_name like concat('%', #{contractName}, '%')
and t4.contract_status = #{contractStatus}
order by t4.contract_date desc limit #{queryCnt}
