mapper.update:
update contract_template
set contract_template_name = #{contractTemplateName,jdbcType=VARCHAR},
  template_file = #{templateFile,jdbcType=VARCHAR}
where contract_templte_id = #{contractTemplteId,jdbcType=VARCHAR}

mapper.select:
select contract_templte_id, contract_template_name, template_file
from contract_template
where contract_templte_id = #{contractTemplteId,jdbcType=VARCHAR}
