mapper.update:
update event_todo_list
set event_type = #{eventType,jdbcType=VARCHAR},
  event_owner = #{eventOwner,jdbcType=VARCHAR},
  event_date = #{eventDate,jdbcType=DATE},
  snapshot = #{snapshot,jdbcType=VARCHAR}
where event_id = #{eventId,jdbcType=VARCHAR}

mapper.select:
select event_id, event_type, event_owner, event_date, snapshot
from event_todo_list
where event_id = #{eventId,jdbcType=VARCHAR}
