mapper.select:
select t1.project_id,t1.project_name, t1.company_id, t1.project_industry, t1.publish_date, t1.period, t1.contract_person,
t1.contract_phone, t1.project_member_count, t1.province,t1.city,t1.district, t1.on_spot, t1.file_material, t1.project_desc,
t1.remark, t1.fee, t1.status, t1.budget,t2.company_name
from project_info t1 ,company_info t2

    t1.company_id = t2.company_id

        and t1.company_id = #{companyId}


      and  t1.project_id = #{projectId}


        and  t1.project_industry = #{industryType}


        and t1. budget >= #{downLimitBudget}


        and t1.budget  <=  #{upperLimitBudget}


        and  t1.status = #{requirementStatus}


        and t1. publish_date >= STR_TO_DATE(#{beginDate}, '%Y%m%d')


        and t1.publish_date  <=  STR_TO_DATE(#{endDate}, '%Y%m%d')

select t2.project_id, t3.company_id, t3.company_name, t3.logo, t2.project_name, t2.project_desc, t2.project_member_count, IFNULL(t4.cnt ,0) project_joined_count, DATE_FORMAT(t2.publish_date,'%Y%m%d') publish_date, t2.status from project_info t2 LEFT JOIN (select count(1) cnt,t3.project_id from worker_info t1,worker_staff_rel t2,project_staff t3 where t1.register_id = t2.register_id and t2.staff_id = t3.staff_id and t3.is_signed = '1' group by project_id) t4 on t2.project_id = t4.project_id,company_info t3
t2.company_id = t3.company_id and t2.status not in ('1','6','7') and t2.project_name like concat('%', #{projectName}, '%')
order by t2.publish_date desc limit 0,#{queryCnt}
