mapper.select:
select project_id, staff_id, is_signed, enter_date, quit_date, salary, status, remark, 
is_settled, quit_reason, staff_name
from project_staff
where project_id = #{projectId,jdbcType=VARCHAR}
  and staff_id = #{staffId,jdbcType=VARCHAR}

select t1.company_id companyId, t2.project_id projectId, t3.staff_id staffId, t1.company_name companyName, t2.project_name projectName, t4.staff_name staffName, t3.is_settled payStatus, t3.remark, case t3.`status` when '0' then '1' else '0' end auditResult
from company_info t1,project_info t2,project_staff t3,staff_resource_pool t4
t1.company_id = t2.company_id and t2.project_id = t3.project_id and t3.staff_id = t4.staff_id
and t2.project_id = #{projectId}
and t3.staff_id = #{staffId}
