mapper.update:
update worker_info
set id_number = #{idNumber,jdbcType=VARCHAR},
  name = #{name,jdbcType=VARCHAR},
  is_certificate = #{isCertificate,jdbcType=VARCHAR}
where register_id = #{registerId,jdbcType=VARCHAR}

mapper.select:
select register_id, id_number, name, is_certificate
from worker_info
where register_id = #{registerId,jdbcType=VARCHAR}
